jQuery(document).ready(function($) {
    $('.item_add').on('click',function(event) {
        // alert($('.item_add').attr('product-id'));
        $.ajax({
            url: 'Carts/AddProductToCart?prId='+$('.item_add').attr('product-id'),
            type: 'GET',
            dataType: 'text',
            success: function(result){
                switch (result) {
                    case "1":
                        $('#simpleCart_quantity').text(parseInt($('#simpleCart_quantity').text())+1);
                        break;
                    case "2":
                        alert('Thêm vào giỏ thất bại vui lòng thử lại sau');
                        break;
                    case "3":
                        alert('Bạn phải đăng nhập để mua hàng!');
                        break;
                }
            }
        })
        
    });
});